import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { FaLinkedin, FaGithub, FaInstagram } from 'react-icons/fa';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');
  const [portfolioData, setPortfolioData] = useState([]);

  const skillsData = [
    { name: 'C++', icon: ' </> ', color: 'text-blue-400', description: 'Strong foundation in object-oriented programming and algorithms.' },
    { name: 'Network', icon: ' ðŸŒ ', color: 'text-green-400', description: 'Familiar with TCP/IP, OSI model, and basic network security.' },
    { name: 'Laravel', icon: ' ðŸ’» ', color: 'text-red-400', description: 'Built CRUD apps and REST APIs using Laravel framework.' },
    { name: 'Python', icon: ' ðŸ ', color: 'text-yellow-400', description: 'Comfortable with scripting and data handling.' },
    { name: 'Git', icon: ' ðŸ™ ', color: 'text-orange-400', description: 'Experience with version control and collaborative workflows.' },
    { name: 'Database', icon: ' ðŸ—„ï¸ ', color: 'text-purple-400', description: 'MySQL and PostgreSQL for relational data management.' },
  ];

  useEffect(() => {
    fetch('http://localhost:5000/api/message')
      .then((res) => res.json())
      .then((data) => setBackendMessage(data.message))
      .catch((err) => console.error('Error fetching message:', err));

    fetch('http://localhost:5000/api/portfolio')
      .then((res) => res.json())
      .then((data) => setPortfolioData(data))
      .catch((err) => console.error('Error fetching portfolio:', err));
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden"
      style={{
        backgroundImage:
          'url("https://placehold.co/1920x1080/1A202C/FFFFFF/png?text=Your+Background+Image")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      <div className="absolute inset-0 bg-black opacity-70"></div>

      {/* About Me */}
      <div className="relative z-10 flex flex-col items-center text-white">
        <img
          src="/Dummy.jpg"
          alt="My profile"
          className="w-40 h-40 rounded-full mb-6 object-cover border-4 border-white shadow-lg"
        />
        <h1 id="About me" className="text-lg font-semibold mb-4 animate-fade-in">About me</h1>
        <p className="text-sm max-w-3xl text-gray-300 animate-fade-in-delay">
          Im Ricarda, an IoT and cybersecurity enthusiast with a passion for robotics and creative technology.
          I love working on smart solutions, combining software, hardware, and imagination.
        </p>
        <p className="text-blue-300 text-sm mt-2 animate-fade-in-delay">
          Backend says: <strong>{backendMessage}</strong>
        </p>
      </div>

      {/* Core Value */}
      <div className="relative z-10 text-left mt-16 max-w-6xl mx-auto text-white">
        <h2 className="text-2xl font-bold mb-8">Core Value</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {[
            { title: 'Leadership', icon: 'ðŸ‘”', text: 'Mampu memimpin dan memotivasi tim...' },
            { title: 'Learning', icon: 'ðŸ’¡', text: 'Selalu ingin belajar hal-hal baru...' },
            { title: 'Criticism', icon: 'ðŸ””', text: 'Mampu menerima dan memberikan kritik...' },
          ].map((item, i) => (
            <div key={i} className="bg-gray-900 rounded-lg p-6">
              <h3 className="text-white text-lg font-semibold mb-2 flex items-center gap-2">
                {item.title} <span className="text-gray-400">{item.icon}</span>
              </h3>
              <p className="text-gray-400 text-sm">{item.text}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Skills */}
      <div className="relative z-10 text-left mt-40 max-w-6xl mx-auto text-white">
        <h2 id="Skill" className="text-2xl font-bold mb-8 text-center animate-fade-in">Skill</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8 animate-fade-in-delay">
          {skillsData.map((skill, index) => (
            <div
              key={index}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transform transition-transform duration-300 hover:scale-105 hover:bg-gray-700 hover:bg-opacity-90"
            >
              <h3 className="text-white text-xl font-semibold mb-3 flex items-center gap-2">
                {skill.name} <span className={`${skill.color}`}>{skill.icon}</span>
              </h3>
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Add your other sections like Portfolio, Contact, SocialMedia below as needed */}
    </section>
  );
}

/* CSS (add to App.css or index.css) */
/*
.animate-fade-in {
  animation: fadeIn 1s ease-in forwards;
  opacity: 0;
}

.animate-fade-in-delay {
  animation: fadeIn 1s ease-in 0.5s forwards;
  opacity: 0;
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}
*/
