import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { FaLinkedin, FaGithub, FaInstagram } from 'react-icons/fa';
import { SplitText } from 'react-bits';
import { useInView } from 'react-intersection-observer';
import { motion, AnimatePresence } from 'framer-motion';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');
  const [portfolioData, setPortfolioData] = useState([]);

  const skillsData = [
    { name: 'C++', icon: ' </> ', color: 'text-blue-400', description: '...' },
    { name: 'Network', icon: ' ðŸŒ ', color: 'text-green-400', description: '...' },
    { name: 'Laravel', icon: ' ðŸ’» ', color: 'text-red-400', description: '...' },
    { name: 'Python', icon: ' ðŸ ', color: 'text-yellow-400', description: '...' },
    { name: 'Git', icon: ' ðŸ™ ', color: 'text-orange-400', description: '...' },
    { name: 'Database', icon: ' ðŸ“”ï¸ ', color: 'text-purple-400', description: '...' },
  ];

  useEffect(() => {
    fetch('http://localhost:5000/api/message')
      .then((res) => res.json())
      .then((data) => setBackendMessage(data.message))
      .catch((err) => console.error('Error fetching message:', err));

    fetch('http://localhost:5000/api/portfolio')
      .then((res) => res.json())
      .then((data) => setPortfolioData(data))
      .catch((err) => console.error('Error fetching portfolio:', err));
  }, []);

  const { ref: aboutRef, inView: aboutInView } = useInView({ triggerOnce: true, threshold: 0.3 });

  return (
    <section
      id="home"
      className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden"
      style={{
        backgroundImage: 'url("https://placehold.co/1920x1080/1A202C/FFFFFF/png?text=Your+Background+Image")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      <div className="absolute inset-0 bg-black opacity-70"></div>

      <div ref={aboutRef} className="relative z-10 flex flex-col items-center text-white">
        <img
          src="/Dummy.jpg"
          alt="My profile"
          className="w-40 h-40 rounded-full mb-6 object-cover border-4 border-white shadow-lg"
        />
        <AnimatePresence>
          {aboutInView && (
            <motion.h1
              id="About me"
              className="text-lg font-semibold mb-4"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <SplitText type="words">About Me</SplitText>
            </motion.h1>
          )}
        </AnimatePresence>

        <AnimatePresence>
          {aboutInView && (
            <motion.p
              className="text-sm max-w-3xl text-gray-300"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4, duration: 0.8 }}
            >
              <SplitText type="words">
                I am Ricarda, an IoT enthusiast passionate about building connected systems and creative solutions in tech!
              </SplitText>
            </motion.p>
          )}
        </AnimatePresence>

        <p className="text-blue-300 text-sm mt-2">
          Backend says: <strong>{backendMessage}</strong>
        </p>
      </div>

      {/* Other sections (Core Value, Skills, Portfolio, Contact, Socials) stay unchanged for now */}
    </section>
  );
}
