import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import axios from 'axios';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');

  const skillsData = [
    { name: 'C++', icon: ' </> ', color: 'text-blue-400', description: 'Strong in object-oriented programming, data structures, and algorithm design.' },
    { name: 'Network', icon: ' 🌐 ', color: 'text-green-400', description: 'Good understanding of TCP/IP, DNS, subnetting, and Wireshark analysis.' },
    { name: 'Laravel', icon: ' 💻 ', color: 'text-red-400', description: 'Experience creating REST APIs, Eloquent ORM, Blade templating, and MVC architecture.' },
    { name: 'Python', icon: ' 🐍 ', color: 'text-yellow-400', description: 'Comfortable in scripting, automation, Flask web apps, and data analysis with Pandas.' },
    { name: 'Git', icon: ' 🐙 ', color: 'text-orange-400', description: 'Version control, branching strategies, and collaborative GitHub workflows.' },
    { name: 'Database', icon: ' 🗄️ ', color: 'text-purple-400', description: 'Experienced with MySQL queries, joins, and database design principles.' },
  ];

  const portfolioData = [
    {
      id: 'project-1',
      title: 'Pump Automation ',
      description: 'Built a home automation system using ESP32 and MQTT protocol.',
      techUsed: ['ESP32', 'MQTT', 'Node-RED'],
    },
    {
      id: 'project-2',
      title: 'Cyber Threat Monitor',
      description: 'A tool to visualize and detect unusual network activities.',
      techUsed: ['Wireshark', 'Python', 'Flask'],
    },
    {
      id: 'project-3',
      title: 'Portfolio Website',
      description: 'Responsive portfolio site built with React and Tailwind CSS.',
      techUsed: ['React', 'Tailwind CSS', 'Node.js'],
    },
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/test')
      .then(res => setBackendMessage(res.data.message))
      .catch(err => console.error(err));
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden scroll-smooth bg-black space-y-40"
    >
      <div className="absolute inset-0 bg-black opacity-70"></div>

      {/* About Me */}
      <motion.div
        initial={{ x: -100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
        className="relative z-10 flex flex-col items-center text-white gap-6"
      >
        <motion.img
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.5, duration: 0.4, ease: 'easeInOut' }}
          src="/Dummy.jpg"
          alt="My profile"
          className="w-40 h-40 rounded-full object-cover border-4 border-white shadow-xl hover:scale-105 transition-transform duration-200 ease-in-out"
        />
        <h1 id="About me" className="text-3xl font-bold">About Me</h1>
        <p className="text-md max-w-3xl text-gray-300">Hi, I’m Ricarda Sebastian Hutagalung an IoT and Developer enthusiast with a creative mindset and passion for automation.</p>
      </motion.div>

      {/* Skills */}
      <motion.div
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="relative z-10 text-left max-w-6xl mx-auto text-white"
      >
        <h2 id="Skill" className="text-2xl font-bold mb-8 text-center">Skill</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {skillsData.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ y: 40, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: index * 0.1, duration: 0.4 }}
              whileHover={{ scale: 1.08 }}
              viewport={{ once: true }}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transition-transform duration-200 ease-in-out hover:bg-gray-700 hover:bg-opacity-90"
            >
              <h3 className="text-white text-xl font-semibold mb-3 flex items-center gap-2">
                {skill.name} <span className={`${skill.color}`}>{skill.icon}</span>
              </h3>
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Portfolio */}
      <motion.div
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="relative z-10 text-left max-w-6xl mx-auto text-white"
      >
        <h2 id="Portfolio" className="text-2xl font-bold mb-8 text-center">Portfolio</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {portfolioData.map((project, index) => (
            <motion.div
              key={index}
              initial={{ y: 40, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: index * 0.1, duration: 0.4 }}
              whileHover={{ scale: 1.08 }}
              viewport={{ once: true }}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transition-transform duration-200 ease-in-out"
            >
              <h3 className="text-white text-lg font-semibold mb-2">{project.title}</h3>
              <p className="text-gray-300 text-sm mb-2">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-2">
                {project.techUsed.map((tech, i) => (
                  <span
                    key={i}
                    className="text-xs bg-blue-600 px-2 py-1 rounded-full text-white"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <Link
                to={`/project/${project.id}`}
                className="inline-block text-sm text-blue-400 hover:underline mt-2"
              >
                View Details →
              </Link>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Contact & Social */}
      <motion.div
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="relative z-10 max-w-3xl mx-auto text-white text-center"
      >
        <h2 id="Contact" className="text-2xl font-bold mb-6">Contact Me</h2>
        <motion.a
          whileHover={{ scale: 1.08 }}
          transition={{ duration: 0.2, ease: 'easeInOut' }}
          href="mailto:ricarda@example.com?subject=Portfolio%20Inquiry&body=Hi%20Ricarda%2C%20I%20visited%20your%20portfolio%20and%20would%20like%20to%20get%20in%20touch."
          className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-6 rounded-lg inline-block transition shadow-md"
        >
          Send Email
        </motion.a>

        <div className="border border-blue-500 mt-10 rounded-lg p-4 flex justify-around text-white bg-gray-800">
          <a href="https://linkedin.com/in/your-link" target="_blank" rel="noopener noreferrer" className="hover:underline">Linkedin</a>
          <a href="https://github.com/yourusername" target="_blank" rel="noopener noreferrer" className="hover:underline">Github</a>
          <a href="https://instagram.com/yourusername" target="_blank" rel="noopener noreferrer" className="hover:underline">Instagram</a>
        </div>
      </motion.div>
    </section>
  );
}
