import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import axios from 'axios';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');

  const hardSkills = [
    { name: 'C++', icon: ' </> ', color: 'text-blue-400', description: 'Strong in object-oriented programming, data structures, and algorithm design.' },
    { name: 'Network', icon: ' ðŸŒ ', color: 'text-green-400', description: 'Good understanding of TCP/IP, DNS, subnetting, and Wireshark analysis.' },
    { name: 'Laravel', icon: ' ðŸ’» ', color: 'text-red-400', description: 'Experience creating REST APIs, Eloquent ORM, Blade templating, and MVC architecture.' },
    { name: 'Python', icon: ' ðŸ ', color: 'text-yellow-400', description: 'Comfortable in scripting, automation, Flask web apps, and data analysis with Pandas.' },
    { name: 'Git', icon: ' ðŸ™ ', color: 'text-orange-400', description: 'Version control, branching strategies, and collaborative GitHub workflows.' },
    { name: 'Database', icon: ' ðŸ—„ï¸ ', color: 'text-purple-400', description: 'Experienced with MySQL queries, joins, and database design principles.' },
  ];

  const softSkills = [
    { name: 'Teamwork', icon: ' ðŸ¤ ', color: 'text-teal-400', description: 'Able to collaborate effectively in group projects and team environments.' },
    { name: 'Communication', icon: ' ðŸ—£ï¸ ', color: 'text-pink-400', description: 'Clear and effective communicator in both written and verbal forms.' },
    { name: 'Problem Solving', icon: ' ðŸ§  ', color: 'text-indigo-400', description: 'Quick to analyze problems and develop efficient, creative solutions.' },
    { name: 'Time Management', icon: ' â° ', color: 'text-lime-400', description: 'Strong ability to prioritize tasks and manage deadlines.' },
  ];

  const portfolioData = [
    {
      id: 'project-1',
      title: 'AMCS (Automated Monitoring & Control System)',
      description: 'An IoT system to monitor and control temperature and humidity using ESP32 and MQTT.',
      techUsed: ['ESP32', 'MQTT', 'Node-RED'],
    },
    {
      id: 'project-2',
      title: 'Car Robotic Obstacle Avoidance',
      description: 'An Arduino-based autonomous car that avoids obstacles using ultrasonic sensors.',
      techUsed: ['Arduino', 'Ultrasonic Sensor', 'Motor Driver'],
    },
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/test')
      .then(res => setBackendMessage(res.data.message))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const mailtoLink = `https://mail.google.com/mail/?view=cm&fs=1&to=ricardasebastian95@gmail.com&su=Portfolio%20Message%20from%20${encodeURIComponent(name)}&body=${encodeURIComponent(message)}`;
    window.open(mailtoLink, '_blank');
  };

  return (
    <section className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden scroll-smooth bg-black">
      {/* About Me */}
      <motion.div
        initial={{ x: -100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
        className="relative z-10 flex flex-col items-center text-white"
      >
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
          className="relative p-4 rounded-3xl overflow-hidden"
        >
          <img
            src="/Dummy.jpg"
            alt="Ricarda"
            className="w-48 h-auto object-contain mx-auto drop-shadow-[0_0_15px_rgba(0,204,255,0.7)]"
          />
        </motion.div>
        <h1 id="About me" className="text-3xl font-bold mt-6 mb-4">About Me</h1>
        <p className="text-md max-w-3xl text-gray-300">Hi, Iâ€™m <span className="text-blue-400 font-semibold">Ricarda</span>, an IoT and cybersecurity enthusiast with a creative mindset and passion for robotics and automation.</p>
        <p className="text-blue-300 text-sm mt-4 italic">
          Backend says: <strong>{backendMessage}</strong>
        </p>
      </motion.div>

      {/* Skills */}
      <motion.div
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="relative z-10 text-left mt-28 max-w-6xl mx-auto text-white"
      >
        <h2 id="Skill" className="text-2xl font-bold mb-8 text-center">Hard Skills</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {hardSkills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ y: 40, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: index * 0.1, duration: 0.3 }}
              viewport={{ once: true }}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transform hover:scale-105 transition-transform duration-200 hover:bg-gray-700 hover:bg-opacity-90"
            >
              <h3 className="text-white text-xl font-semibold mb-3 flex items-center gap-2">
                {skill.name} <span className={`${skill.color}`}>{skill.icon}</span>
              </h3>
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </motion.div>
          ))}
        </div>

        <h2 className="text-2xl font-bold mt-16 mb-8 text-center">Soft Skills</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {softSkills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ y: 40, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: index * 0.1, duration: 0.3 }}
              viewport={{ once: true }}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transform hover:scale-105 transition-transform duration-200 hover:bg-gray-700 hover:bg-opacity-90"
            >
              <h3 className="text-white text-xl font-semibold mb-3 flex items-center gap-2">
                {skill.name} <span className={`${skill.color}`}>{skill.icon}</span>
              </h3>
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Portfolio */}
      {/* ... rest remains unchanged ... */}
    </section>
  );
}
