import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';

const projectDetails = {
  'project-1': {
    title: 'AMCS (Automated Monitoring & Control System)',
    description: [
      'This project focuses on monitoring and controlling temperature and humidity using ESP32, BME280 sensor, and MQTT communication protocol.',
      'I implemented ESP-NOW for fast and lightweight device-to-device communication, with sensor data displayed in real-time via Node-RED dashboard.'
    ],
    images: ['/amcs1.jpg', '/amcs2.jpg', '/amcs3.jpg'],
    video: '/videos/amcs-demo.mp4'
  },
  'project-2': {
    title: 'Car Robotic Obstacle Avoidance',
    description: [
      'This robot car is designed to detect and avoid obstacles using ultrasonic sensors and Arduino.',
      'The project highlights basic robotics control, sensor calibration, and autonomous behavior algorithms.'
    ],
    images: ['/car1.jpg', '/car2.jpg'],
    video: '/videos/car-robotic.mp4'
  },
  'project-3': {
    title: 'Personal Portfolio Website',
    description: [
      'Built a personal website using React, Tailwind CSS, and Framer Motion for animation.',
      'It includes smooth scroll, responsive layout, animated cards, and backend messaging via Node.js.'
    ],
    images: ['/portfolio1.jpg'],
    video: ''
  }
};

export default function ProjectDetail() {
  const { id } = useParams();
  const [current, setCurrent] = useState(0);
  const project = projectDetails[id] || {};

  const nextSlide = () => setCurrent((prev) => (prev + 1) % (project.images?.length || 1));
  const prevSlide = () => setCurrent((prev) => (prev - 1 + (project.images?.length || 1)) % (project.images?.length || 1));

  return (
    <section className="min-h-screen bg-black text-white pt-28 pb-10 px-6 transition-all duration-500 ease-in-out">
      <motion.h1
        className="text-3xl font-extrabold text-center mb-10"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        {project.title || 'Project Detail'}
      </motion.h1>

      {/* Video Preview */}
      {project.video && (
        <div className="max-w-4xl mx-auto mb-12">
          <video controls className="w-full rounded-lg shadow-md">
            <source src={project.video} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}

      {/* Image Slider */}
      {project.images?.length > 0 && (
        <motion.div
          className="relative w-full max-w-3xl mx-auto h-60 md:h-80 bg-gray-800 rounded-xl overflow-hidden mb-10"
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6 }}
        >
          <img
            src={project.images[current]}
            alt="project"
            className="w-full h-full object-cover"
          />
          <button
            className="absolute left-4 top-1/2 -translate-y-1/2 bg-black bg-opacity-40 text-white p-3 rounded-full hover:bg-opacity-60"
            onClick={prevSlide}
          >
            <FaArrowLeft size={20} />
          </button>
          <button
            className="absolute right-4 top-1/2 -translate-y-1/2 bg-black bg-opacity-40 text-white p-3 rounded-full hover:bg-opacity-60"
            onClick={nextSlide}
          >
            <FaArrowRight size={20} />
          </button>
        </motion.div>
      )}

      {/* Descriptions */}
      <div className="mt-12 space-y-6 max-w-3xl mx-auto">
        {project.description?.map((text, i) => (
          <p key={i} className="text-gray-300 text-base leading-relaxed">
            {text}
          </p>
        ))}
      </div>
    </section>
  );
}
