import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');
  const [portfolioData, setPortfolioData] = useState([]);

  const skillsData = [
    {
      name: 'C++',
      icon: ' </> ',
      color: 'text-blue-400',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam molestie elit nec metus venenatis, quis feugiat mauris aliquam.',
    },
    {
      name: 'Network',
      icon: ' ðŸŒ ',
      color: 'text-green-400',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Curabitur vel interdum felis, sit amet auctor nisl.',
    },
    {
      name: 'Laravel',
      icon: ' ðŸ’» ',
      color: 'text-red-400',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris sit amet fermentum velit. Nulla facilisi.',
    },
    {
      name: 'Python',
      icon: ' ðŸ ',
      color: 'text-yellow-400',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum ante ipsum primis in faucibus orci.',
    },
    {
      name: 'Git',
      icon: ' ðŸ™ ',
      color: 'text-orange-400',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin consectetur mauris sit amet quam imperdiet.',
    },
    {
      name: 'Database',
      icon: ' ðŸ—„ï¸ ',
      color: 'text-purple-400',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus vel urna nec leo eleifend malesuada.',
    },
  ];

  useEffect(() => {
    // Fetch greeting message
    fetch('http://localhost:5000/api/message')
      .then((res) => res.json())
      .then((data) => setBackendMessage(data.message))
      .catch((err) => console.error('Error fetching message:', err));

    // Fetch portfolio data
    fetch('http://localhost:5000/api/portfolio')
      .then((res) => res.json())
      .then((data) => setPortfolioData(data))
      .catch((err) => console.error('Error fetching portfolio:', err));
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden"
      style={{
        backgroundImage:
          'url("https://placehold.co/1920x1080/1A202C/FFFFFF/png?text=Your+Background+Image")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      <div className="absolute inset-0 bg-black opacity-70"></div>

      {/* About Me */}
      <div className="relative z-10 flex flex-col items-center text-white">
        <div className="w-40 h-40 rounded-full bg-gray-700 mb-6 flex items-center justify-center text-gray-400 text-6xl">
          {/* Add profile image if needed */}
        </div>
        <h1 id="About me" className="text-lg font-semibold mb-4">
          About me
        </h1>
        <p className="text-sm max-w-3xl text-gray-300">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam molestie
          elit nec metus venenatis, quis feugiat mauris aliquam.
        </p>
        <p className="text-blue-300 text-sm mt-2">
          Backend says: <strong>{backendMessage}</strong>
        </p>
      </div>

      {/* Core Value */}
      <div className="relative z-10 text-left mt-16 max-w-6xl mx-auto text-white">
        <h2 className="text-2xl font-bold mb-8">Core Value</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {[
            { title: 'Leadership', icon: 'ðŸ‘”', text: 'Mampu memimpin dan memotivasi tim...' },
            { title: 'Learning', icon: 'ðŸ’¡', text: 'Selalu ingin belajar hal-hal baru...' },
            { title: 'Criticism', icon: 'ðŸ””', text: 'Mampu menerima dan memberikan kritik...' },
          ].map((item, i) => (
            <div key={i} className="bg-gray-900 rounded-lg p-6">
              <h3 className="text-white text-lg font-semibold mb-2 flex items-center gap-2">
                {item.title} <span className="text-gray-400">{item.icon}</span>
              </h3>
              <p className="text-gray-400 text-sm">{item.text}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Skill */}
      <div className="relative z-10 text-left mt-40 max-w-6xl mx-auto text-white">
        <h2 id="Skill" className="text-2xl font-bold mb-8 text-center">
          Skill
        </h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {skillsData.map((skill, index) => (
            <div
              key={index}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transform transition-transform duration-300 hover:scale-105 hover:bg-gray-700 hover:bg-opacity-90"
            >
              <h3 className="text-white text-xl font-semibold mb-3 flex items-center gap-2">
                {skill.name} <span className={`${skill.color}`}>{skill.icon}</span>
              </h3>
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Portfolio */}
      <div id="portfolio" className="relative z-10 mt-40 max-w-6xl mx-auto text-white text-center">
        <h2 className="text-2xl font-bold mb-14">Portofolio</h2>

        <div className="grid gap-16">
          {portfolioData.map((item, index) => (
            <div
              key={index}
              className="flex flex-col md:flex-row items-center justify-between gap-6 md:gap-10 bg-gray-800 bg-opacity-60 rounded-xl p-6 hover:bg-opacity-80 transition"
            >
              <div className="flex-1 text-left">
                <h3 className="text-xl font-semibold mb-2">Project {index + 1}</h3>
                <p className="text-gray-300 text-sm leading-relaxed">{item.description}</p>
              </div>

              <Link to={`/projects/${item.id}`}>
                <div
                  className={`w-64 h-36 rounded-2xl bg-gray-300 transition-all duration-300 hover:ring-4 cursor-pointer ${
                    item.border ? 'ring-2 ring-blue-400' : 'ring-0'
                  }`}
                ></div>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
