import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';

const images = [
  '/image1.jpg',
  '/image2.jpg',
  '/image3.jpg',
];

export default function ProjectDetail() {
  const { id } = useParams();
  const [current, setCurrent] = useState(0);

  const nextSlide = () => setCurrent((prev) => (prev + 1) % images.length);
  const prevSlide = () => setCurrent((prev) => (prev - 1 + images.length) % images.length);

  return (
    <section className="min-h-screen bg-black text-white py-10 px-6">
      <h1 className="text-2xl font-bold text-center mb-10">{`Project ${id.split('-')[1]}`}</h1>

      {/* Image Slider */}
      <div className="relative w-full max-w-3xl mx-auto h-60 md:h-80 bg-gray-800 rounded-xl flex items-center justify-center">
        <img
          src={images[current]}
          alt="project"
          className="w-full h-full object-cover rounded-xl"
        />
        <button
          className="absolute left-4 bg-black bg-opacity-40 text-white p-2 rounded-full hover:bg-opacity-60"
          onClick={prevSlide}
        >
          <FaArrowLeft />
        </button>
        <button
          className="absolute right-4 bg-black bg-opacity-40 text-white p-2 rounded-full hover:bg-opacity-60"
          onClick={nextSlide}
        >
          <FaArrowRight />
        </button>
      </div>

      <p className="mt-6 max-w-3xl mx-auto text-gray-300 text-sm">
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam molestie elit nec metus venenatis, quis feugiat mauris aliquam.
      </p>
      <p className="mt-4 max-w-3xl mx-auto text-gray-300 text-sm">
        Sed odio eros, porta non molestie eget, gravida vitae est. Nullam at erat in nisi luctus dignissim.
      </p>

      {/* Image + Text Section */}
      <div className="mt-10 grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        <div className="bg-gray-700 h-40" />
        <p className="text-sm text-gray-300">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed odio eros, porta non molestie eget.</p>

        <p className="text-sm text-gray-300">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed odio eros, porta non molestie eget.</p>
        <div className="bg-gray-700 h-40" />

        <div className="bg-gray-700 h-40" />
        <p className="text-sm text-gray-300">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed odio eros, porta non molestie eget.</p>
      </div>
    </section>
  );
}
