import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function ProjectDetail() {
  const { id } = useParams();
  const [project, setProject] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/portfolio/${id}`)
      .then((res) => res.json())
      .then((data) => setProject(data))
      .catch(() => setProject(null));
  }, [id]);

  if (!project) {
    return <p className="text-white mt-20 ml-10">Loading or project not found...</p>;
  }

  return (
    <div className="min-h-screen text-white p-10 bg-black">
      <h1 className="text-3xl font-bold mb-4">Detail Project: {project.id}</h1>
      <p className="text-lg mb-6">{project.description}</p>

      <div className="mb-10">
        <h2 className="text-xl font-semibold mb-2">Tech Used:</h2>
        <ul className="list-disc list-inside text-gray-300">
          {project.techUsed?.map((tech, index) => (
            <li key={index}>{tech}</li>
          ))}
        </ul>
      </div>

      <div
        className={`w-64 h-36 rounded-2xl ${
          project.border ? 'bg-blue-400' : 'bg-gray-400'
        }`}
      ></div>
    </div>
  );
}
