import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');

  const skillsData = [
    { name: 'C++', icon: ' </> ', color: 'text-blue-400', description: 'Strong in object-oriented programming, data structures, and algorithm design.' },
    { name: 'Network', icon: ' 🌐 ', color: 'text-green-400', description: 'Good understanding of TCP/IP, DNS, subnetting, and Wireshark analysis.' },
    { name: 'Laravel', icon: ' 💻 ', color: 'text-red-400', description: 'Experience creating REST APIs, Eloquent ORM, Blade templating, and MVC architecture.' },
    { name: 'Python', icon: ' 🐍 ', color: 'text-yellow-400', description: 'Comfortable in scripting, automation, Flask web apps, and data analysis with Pandas.' },
    { name: 'Git', icon: ' 🐙 ', color: 'text-orange-400', description: 'Version control, branching strategies, and collaborative GitHub workflows.' },
    { name: 'Database', icon: ' 🗄️ ', color: 'text-purple-400', description: 'Experienced with MySQL queries, joins, and database design principles.' },
  ];

  useEffect(() => {
    fetch('http://localhost:5000/api/message')
      .then((res) => res.json())
      .then((data) => setBackendMessage(data.message))
      .catch((err) => console.error('Error fetching message:', err));
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden scroll-smooth bg-black"
    >
      <div className="absolute inset-0 bg-black opacity-70"></div>

      {/* About Me */}
      <motion.div
        initial={{ x: -100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
        className="relative z-10 flex flex-col items-center text-white"
      >
        <motion.img
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.5, duration: 0.6 }}
          src="/Dummy.jpg"
          alt="My profile"
          className="w-40 h-40 rounded-full mb-6 object-cover border-4 border-white shadow-xl hover:scale-105 transition-transform"
        />
        <h1 id="About me" className="text-3xl font-bold mb-4">About Me</h1>
        <p className="text-md max-w-3xl text-gray-300">Hi, I’m <span className="text-blue-400 font-semibold">Ricarda</span>, an IoT and cybersecurity enthusiast with a creative mindset and passion for robotics and automation.</p>
        <p className="text-blue-300 text-sm mt-4 italic">
          Backend says: <strong>{backendMessage}</strong>
        </p>
      </motion.div>

      {/* Skills */}
      <motion.div
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="relative z-10 text-left mt-40 max-w-6xl mx-auto text-white"
      >
        <h2 id="Skill" className="text-2xl font-bold mb-8 text-center">Skill</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {skillsData.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ y: 40, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: index * 0.1, duration: 0.6 }}
              viewport={{ once: true }}
              className="bg-gray-800 bg-opacity-70 rounded-lg p-6 shadow-lg transform hover:scale-105 transition-transform duration-300 hover:bg-gray-700 hover:bg-opacity-90"
            >
              <h3 className="text-white text-xl font-semibold mb-3 flex items-center gap-2">
                {skill.name} <span className={`${skill.color}`}>{skill.icon}</span>
              </h3>
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </section>
  );
}
