import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const projectDetails = {
  'project-1': {
    title: 'Project 1',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit...',
    sections: [
      { text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit...', img: true },
      { text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit...', img: false },
      { text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit...', img: true },
    ],
  },
  'project-2': {
    title: 'Project 2',
    description: 'Detailed info about project 2...',
    sections: [],
  },
  'project-3': {
    title: 'Project 3',
    description: 'Detailed info about project 3...',
    sections: [],
  },
};

export default function ProjectDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const project = projectDetails[id];

  if (!project) {
    return <div className="text-white p-10">Project not found</div>;
  }

  return (
    <section className="min-h-screen bg-black text-white px-6 py-10">
      <div className="text-center mb-10">
        <h1 className="text-3xl font-bold">{project.title}</h1>
      </div>

      {/* Top Image */}
      <div className="bg-gray-700 h-32 rounded-xl flex items-center justify-between px-4 mb-6">
        <button
          onClick={() => navigate(-1)}
          className="text-white text-xl"
        >
          ←
        </button>
        <button
          onClick={() => navigate('/')}
          className="text-white text-xl"
        >
          →
        </button>
      </div>

      <p className="mb-8 px-2 text-gray-300">{project.description}</p>

      {/* Sections with images and text */}
      <div className="space-y-8">
        {project.sections.map((section, idx) => (
          <div
            key={idx}
            className="flex flex-col md:flex-row items-center gap-6"
          >
            {section.img && (
              <div className="w-full md:w-1/2 h-32 bg-gray-600 rounded-lg" />
            )}
            <p className="text-gray-300 text-sm w-full md:w-1/2">{section.text}</p>

            {!section.img && (
              <div className="w-full md:w-1/2 h-32 bg-gray-600 rounded-lg" />
            )}
          </div>
        ))}
      </div>
    </section>
  );
}
