import React, { useEffect, useState } from 'react';
import { Fade } from 'react-awesome-reveal';
import { FaLinkedin, FaGithub, FaInstagram } from 'react-icons/fa';

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');
  const [portfolioData, setPortfolioData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/message')
      .then((res) => res.json())
      .then((data) => setBackendMessage(data.message))
      .catch((err) => console.error('Error fetching message:', err));

    fetch('http://localhost:5000/api/portfolio')
      .then((res) => res.json())
      .then((data) => setPortfolioData(data))
      .catch((err) => console.error('Error fetching portfolio:', err));
  }, []);

  return (
    <section
      id="home"
      className="relative min-h-screen pt-32 pb-16 px-4 text-center overflow-hidden"
      style={{
        backgroundImage:
          'url("https://placehold.co/1920x1080/1A202C/FFFFFF/png?text=Your+Background+Image")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      <div className="absolute inset-0 bg-black opacity-70"></div>

      {/* About Me */}
      <Fade triggerOnce direction="up">
        <div className="relative z-10 flex flex-col items-center text-white">
          <img
            src="/Dummy.jpg"
            alt="My profile"
            className="w-40 h-40 rounded-full mb-6 object-cover border-4 border-white shadow-lg"
          />
          <h1 id="About me" className="text-lg font-semibold mb-4">About me</h1>
          <p className="text-sm max-w-3xl text-gray-300">Lorem ipsum dolor sit amet...</p>
          <p className="text-blue-300 text-sm mt-2">
            Backend says: <strong>{backendMessage}</strong>
          </p>
        </div>
      </Fade>

      {/* Core Value */}
      <Fade triggerOnce direction="up">
        <div className="relative z-10 text-left mt-16 max-w-6xl mx-auto text-white">
          <h2 className="text-2xl font-bold mb-8">Core Value</h2>
          <div className="grid md:grid-cols-3 gap-8">
            {[{
              title: 'Leadership', icon: 'ðŸ‘”', text: 'Mampu memimpin dan memotivasi tim...'
            }, {
              title: 'Learning', icon: 'ðŸ’¡', text: 'Selalu ingin belajar hal-hal baru...'
            }, {
              title: 'Criticism', icon: 'ðŸ””', text: 'Mampu menerima dan memberikan kritik...'
            }].map((item, i) => (
              <div key={i} className="bg-gray-900 rounded-lg p-6">
                <h3 className="text-white text-lg font-semibold mb-2 flex items-center gap-2">
                  {item.title} <span className="text-gray-400">{item.icon}</span>
                </h3>
                <p className="text-gray-400 text-sm">{item.text}</p>
              </div>
            ))}
          </div>
        </div>
      </Fade>

      {/* Portfolio */}
      <Fade triggerOnce direction="up">
        <div id="portfolio" className="relative z-10 mt-40 max-w-6xl mx-auto text-white text-center">
          <h2 className="text-2xl font-bold mb-14">Portofolio</h2>
          <div className="grid gap-16">
            {portfolioData.map((item, index) => (
              <div
                key={index}
                className="flex flex-col md:flex-row items-center justify-between gap-6 md:gap-10 bg-gray-800 bg-opacity-60 rounded-xl p-6 hover:bg-opacity-80 transition"
              >
                <div className="flex-1 text-left">
                  <h3 className="text-xl font-semibold mb-2">Project {index + 1}</h3>
                  <p className="text-gray-300 text-sm leading-relaxed">{item.description}</p>
                </div>
                <div
                  className={`w-64 h-36 rounded-2xl bg-gray-300 transition-all duration-300 hover:ring-4 cursor-pointer ${
                    item.border ? 'ring-2 ring-blue-400' : 'ring-0'
                  }`}
                ></div>
              </div>
            ))}
          </div>
        </div>
      </Fade>

      {/* Contact */}
      <Fade triggerOnce direction="up">
        <div id="contact" className="relative z-10 mt-40 max-w-4xl mx-auto text-white text-left">
          <h2 className="text-2xl font-bold mb-8 text-center">Contact Me</h2>
          <form
            className="bg-gray-900 bg-opacity-70 rounded-lg p-8 shadow-lg"
            onSubmit={async (e) => {
              e.preventDefault();
              const form = e.target;
              const formData = {
                name: form.name.value,
                email: form.email.value,
                message: form.message.value,
              };

              try {
                const res = await fetch('http://localhost:5000/api/contact', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(formData),
                });

                const result = await res.json();
                alert(result.message);
                if (res.ok) form.reset();
              } catch (error) {
                alert('Failed to send message');
                console.error(error);
              }
            }}
          >
            <div className="grid md:grid-cols-2 gap-6 mb-6">
              <div>
                <label className="block mb-2 text-sm">Name</label>
                <input type="text" name="name" required className="w-full p-3 rounded bg-gray-800 text-white border border-gray-700 focus:ring-2 focus:ring-blue-400" />
              </div>
              <div>
                <label className="block mb-2 text-sm">Email</label>
                <input type="email" name="email" required className="w-full p-3 rounded bg-gray-800 text-white border border-gray-700 focus:ring-2 focus:ring-blue-400" />
              </div>
            </div>
            <div className="mb-6">
              <label className="block mb-2 text-sm">Message</label>
              <textarea name="message" rows="5" required className="w-full p-3 rounded bg-gray-800 text-white border border-gray-700 focus:ring-2 focus:ring-blue-400"></textarea>
            </div>
            <button type="submit" className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg transition">Send Message</button>
          </form>
        </div>
      </Fade>

      {/* Social Media */}
      <Fade triggerOnce direction="up">
        <div className="relative z-10 mt-24 mb-10 flex justify-center">
          <div className="bg-gray-900 bg-opacity-80 rounded-3xl px-10 py-6 w-full max-w-xl shadow-lg flex flex-col items-center">
            <p className="text-white text-lg font-semibold mb-4">Connect with me</p>
            <div className="flex justify-center gap-10">
              <a href="https://www.linkedin.com/in/yourusername" target="_blank" rel="noopener noreferrer" className="text-white hover:text-blue-400">
                <FaLinkedin size={28} />
              </a>
              <a href="https://github.com/yourgithub" target="_blank" rel="noopener noreferrer" className="text-white hover:text-gray-400">
                <FaGithub size={28} />
              </a>
              <a href="https://instagram.com/yourinstagram" target="_blank" rel="noopener noreferrer" className="text-white hover:text-pink-400">
                <FaInstagram size={28} />
              </a>
            </div>
          </div>
        </div>
      </Fade>
    </section>
  );
}
