import React from 'react';
import { useParams } from 'react-router-dom';

const projects = {
  'project-1': {
    title: 'Smart Home IoT',
    description: 'Built a home automation system using ESP32 and MQTT protocol.',
    details: 'This project uses ESP32 to control home appliances and communicates with Node-RED through MQTT. It features real-time status monitoring and auto control logic for humidity and temperature.',
    tech: ['ESP32', 'MQTT', 'Node-RED']
  },
  'project-2': {
    title: 'Cyber Threat Monitor',
    description: 'A tool to visualize and detect unusual network activities.',
    details: 'It includes a Python-based backend that parses PCAP files and displays alerts based on traffic anomalies, ideal for beginner cybersecurity projects.',
    tech: ['Wireshark', 'Python', 'Flask']
  },
  'project-3': {
    title: 'Portfolio Website',
    description: 'Responsive portfolio site built with React and Tailwind CSS.',
    details: 'Fully responsive design, includes contact form, animation, and skill/project section. Hosted with Docker and backend for message handling.',
    tech: ['React', 'Tailwind CSS', 'Node.js']
  }
};

export default function ProjectDetail() {
  const { id } = useParams();
  const project = projects[id];

  if (!project) return <div className="text-white text-center mt-20">Project not found</div>;

  return (
    <div className="min-h-screen px-4 pt-32 text-white bg-black">
      <h1 className="text-3xl font-bold mb-4">{project.title}</h1>
      <p className="text-lg mb-4">{project.description}</p>
      <p className="text-gray-300 mb-6">{project.details}</p>
      <div className="flex gap-2 flex-wrap">
        {project.tech.map((tech, i) => (
          <span key={i} className="bg-blue-600 text-white text-xs px-2 py-1 rounded-full">{tech}</span>
        ))}
      </div>
    </div>
  );
}
