import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import axios from 'axios';

function renderStars(level) {
  const total = 5;
  return (
    <div className="text-yellow-400 text-lg mb-1">
      {[...Array(total)].map((_, i) => (
        <span key={i}>{i < level ? '★' : '☆'}</span>
      ))}
    </div>
  );
}

export function Home() {
  const [backendMessage, setBackendMessage] = useState('');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [showMenu, setShowMenu] = useState(false);

  const hardSkills = [
    { name: 'C++', level: 4, description: 'Strong in object-oriented programming, data structures, and algorithm design.' },
    { name: 'Network', level: 3, description: 'Good understanding of TCP/IP, DNS, subnetting, and Wireshark analysis.' },
    { name: 'Laravel', level: 3, description: 'Experience with REST APIs, Eloquent ORM, Blade, and MVC.' },
    { name: 'Python', level: 4, description: 'Comfortable in scripting, Flask web apps, and data analysis with Pandas.' },
    { name: 'Git', level: 5, description: 'Version control, branching, and collaborative workflows.' },
    { name: 'Database', level: 3, description: 'MySQL queries, joins, and database design.' },
  ];

  const softSkills = [
    { name: 'Teamwork', level: 4, description: 'Able to collaborate effectively in team environments.' },
    { name: 'Communication', level: 4, description: 'Clear communicator in written and verbal forms.' },
    { name: 'Problem Solving', level: 3, description: 'Quick to analyze and solve challenges.' },
    { name: 'Time Management', level: 5, description: 'Prioritizes tasks and manages deadlines well.' },
  ];

  const portfolioData = [
    {
      id: 'project-1',
      title: 'AMCS (Automated Monitoring & Control System)',
      description: 'An IoT system to monitor and control temperature and humidity using ESP32 and MQTT.',
      techUsed: ['ESP32', 'MQTT', 'Node-RED'],
    },
    {
      id: 'project-2',
      title: 'Car Robotic Obstacle Avoidance',
      description: 'An Arduino-based autonomous car that avoids obstacles using ultrasonic sensors.',
      techUsed: ['Arduino', 'Ultrasonic Sensor', 'Motor Driver'],
    },
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/test')
      .then(res => setBackendMessage(res.data.message))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const mailtoLink = `https://mail.google.com/mail/?view=cm&fs=1&to=ricardasebastian95@gmail.com&su=Portfolio%20Message%20from%20${encodeURIComponent(name)}&body=${encodeURIComponent(message)}`;
    window.open(mailtoLink, '_blank');
  };

  return (
    <section className="min-h-screen bg-black text-white scroll-smooth">
      {/* Navbar */}
      <header className="fixed top-0 left-0 right-0 bg-[#0d1117] z-50 shadow-md">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <div className="text-white text-2xl font-bold tracking-wide">PROTOFILO.</div>
          <nav className="hidden md:flex space-x-6 text-sm font-semibold tracking-wide">
            {['Home', 'Skills', 'Works', 'Contact'].map((item) => (
              <a
                key={item}
                href={`#${item.toLowerCase()}`}
                className="text-white hover:text-yellow-400 transition-colors duration-300"
              >
                {item.toUpperCase()}
              </a>
            ))}
          </nav>
          <div className="md:hidden">
            <button onClick={() => setShowMenu(!showMenu)} className="text-white text-2xl">☰</button>
          </div>
        </div>
        {showMenu && (
          <div className="md:hidden bg-[#0d1117] text-center py-4 space-y-4">
            {['Home', 'Skills', 'Works', 'Contact'].map((item) => (
              <a
                key={item}
                href={`#${item.toLowerCase()}`}
                onClick={() => setShowMenu(false)}
                className="block text-white hover:text-yellow-400 transition-colors duration-300"
              >
                {item.toUpperCase()}
              </a>
            ))}
          </div>
        )}
      </header>

      {/* Hero + About Section Combined */}
      <div id="home" className="flex flex-col lg:flex-row items-center justify-center gap-12 max-w-7xl mx-auto pt-32 pb-20 px-4">
        <img
          src="/result.png"
          alt="Ricarda"
          className="w-[280px] lg:w-[360px] object-contain"
        />
        <motion.div
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
          className="text-left max-w-xl space-y-5"
        >
          <p className="text-yellow-400 text-lg">MY NAME IS</p>
          <h1 className="text-4xl md:text-5xl font-bold">RICARDA</h1>
          <p className="bg-yellow-400 text-black font-semibold inline-block px-4 py-1 rounded">
            I’M A UI & IOT DEVELOPER
          </p>
          <p className="text-gray-300">
            Hello, I’m <span className="text-white font-semibold">Ricarda</span>, a creative developer with a deep passion for IoT, UI design, and robotics. Ever since I was a kid, I’ve been inspired by Japanese robot anime and engineering. I specialize in developing user-friendly interfaces and smart embedded systems with a secure-by-design approach.
          </p>
        </motion.div>
      </div>

      {/* Skills Section */}
      <motion.div id="skills" className="mt-12 text-white max-w-6xl mx-auto px-4" initial={{ opacity: 0 }} whileInView={{ opacity: 1 }} transition={{ duration: 1 }} viewport={{ once: true }}>
        <h2 className="text-2xl font-bold mb-8 text-center">Hard Skills</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {hardSkills.map((skill, index) => (
            <motion.div key={index} initial={{ y: 40, opacity: 0 }} whileInView={{ y: 0, opacity: 1 }} transition={{ delay: index * 0.1, duration: 0.3 }} viewport={{ once: true }} className="bg-gray-800 rounded-lg p-6 shadow-lg hover:scale-105 transition duration-200">
              <h3 className="text-xl font-semibold">{skill.name}</h3>
              {renderStars(skill.level)}
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </motion.div>
          ))}
        </div>

        <h2 className="text-2xl font-bold mt-16 mb-8 text-center">Soft Skills</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {softSkills.map((skill, index) => (
            <motion.div key={index} initial={{ y: 40, opacity: 0 }} whileInView={{ y: 0, opacity: 1 }} transition={{ delay: index * 0.1, duration: 0.3 }} viewport={{ once: true }} className="bg-gray-800 rounded-lg p-6 shadow-lg hover:scale-105 transition duration-200">
              <h3 className="text-xl font-semibold">{skill.name}</h3>
              {renderStars(skill.level)}
              <p className="text-gray-300 text-sm">{skill.description}</p>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Works Section */}
      <motion.div id="works" className="mt-24 text-white max-w-6xl mx-auto px-4" initial={{ opacity: 0 }} whileInView={{ opacity: 1 }} transition={{ duration: 1 }} viewport={{ once: true }}>
        <h2 className="text-2xl font-bold mb-8 text-center">Portfolio</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {portfolioData.map((project, index) => (
            <motion.div key={index} initial={{ y: 40, opacity: 0 }} whileInView={{ y: 0, opacity: 1 }} transition={{ delay: index * 0.1, duration: 0.3 }} viewport={{ once: true }} className="bg-gray-800 rounded-lg p-6 shadow-lg hover:scale-105 transition duration-200">
              <h3 className="text-lg font-semibold mb-2">{project.title}</h3>
              <p className="text-gray-300 text-sm mb-2">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-2">
                {project.techUsed.map((tech, i) => (
                  <span key={i} className="text-xs bg-blue-600 px-2 py-1 rounded-full text-white">{tech}</span>
                ))}
              </div>
              <Link to={`/project/${project.id}`} className="text-sm text-blue-400 hover:underline">View Details →</Link>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Contact Section */}
      <motion.div id="contact" className="mt-24 max-w-3xl mx-auto text-white text-center px-4" initial={{ opacity: 0 }} whileInView={{ opacity: 1 }} transition={{ duration: 1 }} viewport={{ once: true }}>
        <h2 className="text-2xl font-bold mb-6">Contact Me</h2>
        <form onSubmit={handleSubmit} className="bg-gray-900 p-6 rounded-xl shadow-lg flex flex-col gap-4">
          <input type="text" placeholder="Your Name" className="p-2 rounded bg-gray-700 text-white" value={name} onChange={(e) => setName(e.target.value)} required />
          <textarea placeholder="Your Message" className="p-2 rounded bg-gray-700 text-white" rows={4} value={message} onChange={(e) => setMessage(e.target.value)} required />
          <button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded w-1/3 mx-auto">Send Message</button>
        </form>
        <div className="border border-blue-500 mt-10 rounded-lg p-4 flex justify-around bg-gray-800">
          <a href="https://www.linkedin.com/in/ricarda-sebastian-hutagalung-2b503b2b6" target="_blank" rel="noopener noreferrer" className="hover:underline">LinkedIn</a>
          <a href="https://github.com/Riffk" target="_blank" rel="noopener noreferrer" className="hover:underline">GitHub</a>
          <a href="#" className="hover:underline">Instagram</a>
        </div>
      </motion.div>
    </section>
  );
}
